const db = require('../models/index');
const getPost = async (req, res) => {
  try {
      const data = await db.posts.find().populate('createdBy')
      res.status(200).json({ status: "SUCCESS", data: data });
  } catch (error) {
      res.json({error})
  }
};
const getPostById = async (req, res) => {
    try {
      const postId = req.body.id
      const data = await db.posts.findById({postId}).populate('comments');
      res.json({data})
    } catch (error) {
      res.json({error})
    }
};
const InsertData = async (req, res) => {
  try {
    const data = await db.posts.create({
      title: req.body.title,
      description: req.body.description,
      createdAt: Date.now(),
      createdBy: req.body.createdBy
    })
  } catch (error) {
    res.json({error})
  }
};
const UpdateData = async (req, res) => {
  try {
    const paramsId = req.body.id
    const data = await db.posts.findByIdAndUpdate(paramsId, {title: req.body.title,description: req.body.description});
    res.json({data})
  } catch (error) {
    res.json({error})
  }
};
const DeleteData = async (req, res) => {
    try {
      const objId = req.body.id
      const data = await db.posts.deleteOne({objId})
      res.json({message: 'success'})
    } catch (error) {
      res.json({error})
    }
};
module.exports = {
    getPost,
    getPostById,
    InsertData,
    UpdateData,
    DeleteData
}