const express = require('express');
const Routes  = express.Router();
const Controller = require('../controller/index');
Routes.get('/getComments', function (req, res){
    Controller.UserController.getComments(req, res)
})
Routes.get('/getbyId/:id', function(req, res){
    Controller.UserController.getCommentsById(req, res)
})
Routes.post('/insertData', function (req, res){
    Controller.UserController.InsertData(req, res)
})
Routes.post('/updateData/:id', function (req, res) {
    Controller.UserController.UpdateData(req, res)
})
Routes.post('/DeleteData', function (req, res) {
    Controller.UserController.DeleteData(req, res)
})
module.exports = Routes