const express = require('express');
const Routes  = express.Router();
const Controller = require('../controller/index');
Routes.get('/getPost', function (req, res){
    Controller.PostController.getPost(req, res)
})
Routes.get('/getbyId/:id', function(req, res){
    Controller.PostController.getPostById(req, res)
})
Routes.post('/insertData', function (req, res){
    Controller.PostController.InsertData(req, res)
})
Routes.post('/updateData/:id', function (req, res) {
    Controller.PostController.UpdateData(req, res)
})
Routes.post('/DeleteData', function (req, res) {
    Controller.PostController.DeleteData(req, res)
})
module.exports = Routes