const express = require('express');
const Routes  = express.Router();
const Controller = require('../controller/index');
Routes.get('/getUser', function (req, res){
    Controller.UserController.getUser(req, res)
})
Routes.post('/login', function (req, res){
    Controller.UserController.loginUser(req, res)
})
Routes.post('/register', function (req, res){
    Controller.UserController.RegisterUser(req, res)
})
Routes.get('/getbyId/:id', function(req, res){
    Controller.UserController.getUserById(req, res)
})
Routes.post('/updateData/:id', function (req, res) {   
    Controller.UserController.UpdateData(req, res)
})
Routes.post('/DeleteData', function (req, res) {
    Controller.UserController.DeleteData(req, res)
})
module.exports = Routes